import React from 'react';
import { render } from 'enzyme';
import { requiredProps } from '../../../test/required_props';
import { EuiFormFieldset } from './form_fieldset';
describe('EuiFormFieldset', function () {
  test('is rendered', function () {
    var component = render(React.createElement(EuiFormFieldset, requiredProps, React.createElement("input", null)));
    expect(component).toMatchSnapshot();
  });
  describe('props', function () {
    test('legend is rendered', function () {
      var component = render(React.createElement(EuiFormFieldset, {
        legend: {
          children: 'Legend'
        }
      }, React.createElement("input", null)));
      expect(component).toMatchSnapshot();
    });
  });
});