import React from 'react';
import { render } from 'enzyme';
import { requiredProps } from '../../../test/required_props';
import { EuiFormLegend } from './form_legend';
describe('EuiFormLegend', function () {
  test('is rendered', function () {
    var component = render(React.createElement(EuiFormLegend, requiredProps, "Legend"));
    expect(component).toMatchSnapshot();
  });
  describe('props', function () {
    test('hidden is rendered', function () {
      var component = render(React.createElement(EuiFormLegend, {
        display: "hidden"
      }, "Legend"));
      expect(component).toMatchSnapshot();
    });
    test('compressed is rendered', function () {
      var component = render(React.createElement(EuiFormLegend, {
        compressed: true
      }, "Legend"));
      expect(component).toMatchSnapshot();
    });
  });
});